AFAnalysisRule.ConfigString:
NotificationStatus := IF '{fc0db3a7-af09-4f42-b996-52ecc91f9645};RunHours Since Maintenance' > '{fc0db3a7-af09-4f42-b996-52ecc91f9645};RunHours Since Maintenance|{74940458-1a0f-4839-b0d2-af33108e65e2};RunHours Maintenance Trigger' 
THEN "Alarm" 
ELSE "Good";

LastMaintRuntime := TimeEq('{2acc6453-5c53-40a8-a10e-d5b0d2582948};Pump Status','{7662ae47-de27-40cf-9ea3-b5765dddef12};Maintenance Date','*',"ON")/3600;
InstallationRuntime := TimeEq('{2acc6453-5c53-40a8-a10e-d5b0d2582948};Pump Status','{09291866-1aa5-4988-8c79-d7fbdafee9f0};Installation Date','*',"ON")/3600;
NumberofStarts := EventCount('{2acc6453-5c53-40a8-a10e-d5b0d2582948};Pump Status','{7662ae47-de27-40cf-9ea3-b5765dddef12};Maintenance Date','*')/2;

RatedCurrentDraw := // Typical Current Draw of the Pump
25;
RandomEq := Rand();
CurrentDrawEq := IF RandomEq > .33 THEN Rand(RatedCurrentDraw,1) ELSE 0;

RatedFlowRate := // Typical Flow Rate of the Pump
450;
RatedPressure := // Typical Pressure of the Pump
150;
RatedTemperature := // Typical Temperature of the Pump
170;
FlowRate := IF '{2acc6453-5c53-40a8-a10e-d5b0d2582948};Pump Status' = "ON" THEN Rand(RatedFlowRate,35) ELSE 0;
Pressure := IF '{2acc6453-5c53-40a8-a10e-d5b0d2582948};Pump Status' = "ON" THEN Rand(RatedPressure,10) ELSE Rand(.1,.2);
PumpStatusSeconds := SecSinceChange('{2acc6453-5c53-40a8-a10e-d5b0d2582948};Pump Status');
Temperature := IF '{2acc6453-5c53-40a8-a10e-d5b0d2582948};Pump Status' = "ON" and '{e731bb63-e2f9-4bca-b5f8-1de47c3a3ae8};Bearing Temperature' < RatedTemperature
THEN '{e731bb63-e2f9-4bca-b5f8-1de47c3a3ae8};Bearing Temperature'+Rand(5,2)
ELSE IF '{2acc6453-5c53-40a8-a10e-d5b0d2582948};Pump Status' = "ON" and '{e731bb63-e2f9-4bca-b5f8-1de47c3a3ae8};Bearing Temperature' > RatedTemperature
THEN Rand(RatedTemperature,6) 
ELSE If '{2acc6453-5c53-40a8-a10e-d5b0d2582948};Pump Status' = "OFF"
THEN ( (Rand(1,.1)*RatedTemperature - 90) * Exp(-.001*PumpStatusSeconds)) + 90
ELSE NoOutput();
